import React from "react";

class Edit extends React.Component {
    render() {

        const newsTemplate = this.props.data.map(function (item, index) {
            return (<div key={index}><p className="news__author">{item.author}:</p><p
                className="news__text">{item.text}</p></div>)
        })
        return (
            <div>
                <div>Edaait {this.props.length}</div>


            </div>
        );
    }
}

export default Edit;
