import React from "react";
import Info from "./components/info"
import Search from "./components/search"
import Results from "./components/results"
import Edit from "./components/edit"
import Create from "./components/create"
import Form from "./components/form";

class App extends React.Component {

    state = {
        notes: null,
        idNote: undefined,
        dateNote: undefined,
        urgencyNote: undefined,
        textNote: undefined,
        error: undefined
    }

    gettingWeather = async (e) => {
        e.preventDefault();
        const ddd = e.target.city.value;
        console.log("test0 ", e.target.elements.city);
        console.log("test ", ddd);
        const api_url = await fetch(`http://127.0.0.1:8080/message`, {cache: 'no-cache'}); //, mode: 'no-cors'
        //api_url.then(function(data){console.log(data, data.json())});
        //console.log("1 _ ", await api_url.json());
        const data = await api_url.json();

        this.state.news = data;

        const listNotes =
            data.map((postDetail, index) => {
                return console.log("куку", postDetail.idNote)

            });


        console.log("res1 ", api_url);
        //console.log("res2 ", api_url.text());
        console.log("привет2 ", data);

        //console.log("data.idNote1", data.Soci);
        console.log("data.idNote2", data.textNote);
        console.log("data.idNote3", data.urgencyNote);
        console.log("data.idNote4", data.dateNote);


        this.setState({
            idNote: data.idNote,
            dateNote: data.dateNote,
            urgencyNote: data.urgencyNote,
            textNote: data.textNote,
            error: ""
        });
    }



    render() {
        this.setState({notes: this.gettingWeather.data});
        console.log("this.state.notes " + this.state.notes);

        return (
            <div>
                <Info/>
                <Form weatherMethod={this.gettingWeather}/>
                <Search weatherMethod={this.gettingWeather}/>
                <Results
                    sss={this.state.notes}
                />
                <Edit dataNews={this.gettingWeather}/>
                <Create/>
            </div>
        )
    }

}

export default App;
